import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import NewPost from "./pages/NewPost";
import { motion, AnimatePresence } from "framer-motion";

function App() {
  const [isScrolled, setIsScrolled] = useState(false);

  // Handle navbar transparency on scroll
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Router>
      <AppContent isScrolled={isScrolled} />
    </Router>
  );
}

function AppContent({ isScrolled }) {
  const location = useLocation();

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 font-sans flex flex-col">
      {/* Navbar */}
      <nav 
        className={`fixed w-full z-50 transition-all duration-300 ${
          isScrolled 
            ? "bg-white/90 backdrop-blur-md shadow-lg text-gray-800" 
            : "bg-transparent text-gray-800"
        } px-6 py-4`}
      >
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <Link to="/" className="flex items-center space-x-2">
            <span className="text-3xl">ðŸ§±</span>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-500 bg-clip-text text-transparent">
              Muro Interactivo
            </h1>
          </Link>

          <div className="hidden md:flex space-x-1">
            <NavLink to="/" currentPath={location.pathname}>
              Muro
            </NavLink>
            <NavLink to="/login" currentPath={location.pathname}>
              Iniciar sesiÃ³n
            </NavLink>
            <NavLink to="/register" currentPath={location.pathname}>
              Registrarse
            </NavLink>
            <Link
              to="/new-post"
              className="bg-gradient-to-r from-blue-600 to-indigo-500 text-white px-5 py-2 rounded-full hover:shadow-lg transition duration-300 flex items-center space-x-1"
            >
              <span>+</span>
              <span>Nuevo post</span>
            </Link>
          </div>

          {/* Mobile menu button - would need implementation */}
          <button className="md:hidden text-gray-800">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </nav>

      {/* Main content with page transitions */}
      <main className="flex-grow pt-24 pb-12 px-6 max-w-6xl mx-auto w-full">
        <AnimatePresence mode="wait">
          <motion.div
            key={location.pathname}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
            className="w-full"
          >
            <Routes location={location}>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/new-post" element={<NewPost />} />
              <Route
                path="*"
                element={
                  <div className="text-center py-20">
                    <h2 className="text-3xl font-bold text-gray-700">PÃ¡gina no encontrada</h2>
                    <p className="mt-4 text-gray-500">La pÃ¡gina que buscas no existe o ha sido movida.</p>
                    <Link to="/" className="mt-6 inline-block bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700 transition">
                      Volver al inicio
                    </Link>
                  </div>
                }
              />
            </Routes>
          </motion.div>
        </AnimatePresence>
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-10">
        <div className="max-w-6xl mx-auto px-6">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div className="mb-6 md:mb-0">
              <h2 className="text-xl font-bold flex items-center">
                <span className="text-2xl mr-2">ðŸ§±</span> Muro Interactivo
              </h2>
              <p className="text-gray-400 mt-2">Comparte tus ideas con el mundo</p>
            </div>
            <div className="flex space-x-4">
              <SocialIcon icon="facebook" />
              <SocialIcon icon="twitter" />
              <SocialIcon icon="instagram" />
              <SocialIcon icon="github" />
            </div>
          </div>
          <div className="border-t border-gray-800 mt-8 pt-8 flex flex-col md:flex-row justify-between items-center">
            <p className="text-sm text-gray-400">Â© 2025 Muro Interactivo. Todos los derechos reservados.</p>
            <div className="mt-4 md:mt-0 flex space-x-6 text-sm text-gray-400">
              <a href="#" className="hover:text-white transition">Privacidad</a>
              <a href="#" className="hover:text-white transition">TÃ©rminos</a>
              <a href="#" className="hover:text-white transition">Contacto</a>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
}

// Helper components
function NavLink({ to, children, currentPath }) {
  const isActive = currentPath === to;

  return (
    <Link
      to={to}
      className={`relative px-4 py-2 rounded-full transition duration-300 ${
        isActive 
          ? "text-blue-600 font-medium" 
          : "text-gray-600 hover:text-blue-600"
      }`}
    >
      {children}
      {isActive && (
        <motion.span 
          layoutId="navIndicator"
          className="absolute bottom-0 left-0 right-0 h-0.5 bg-blue-600 rounded-full"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
        />
      )}
    </Link>
  );
}

function SocialIcon({ icon }) {
  return (
    <a href="#" className="w-10 h-10 rounded-full bg-gray-800 flex items-center justify-center hover:bg-blue-600 transition duration-300">
      <span className="sr-only">{icon}</span>
      <svg className="w-5 h-5 fill-current" viewBox="0 0 24 24">
        {/* This would be replaced with actual social icons */}
        <path d="M12 2C6.477 2 2 6.477 2 12c0 5.523 4.477 10 10 10s10-4.477 10-10c0-5.523-4.477-10-10-10z" />
      </svg>
    </a>
  );
}

export default App;
