import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import NewPost from "./pages/NewPost";

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-100 font-sans">
        {/* Navbar */}
        <nav className="bg-white shadow-md px-4 py-3 flex justify-between items-center">
          <h1 className="text-xl font-bold text-blue-600">üß± Muro Interactivo</h1>
          <div className="space-x-4">
            <Link to="/" className="text-gray-700 hover:text-blue-500">Muro</Link>
            <Link to="/login" className="text-gray-700 hover:text-blue-500">Iniciar sesi√≥n</Link>
            <Link to="/register" className="text-gray-700 hover:text-blue-500">Registrarse</Link>
            <Link to="/new-post" className="text-gray-700 hover:text-blue-500">Nuevo post</Link>
          </div>
        </nav>

        {/* Main content */}
        <main className="p-4 max-w-4xl mx-auto">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/new-post" element={<NewPost />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
